INSERT INTO `sm_article` VALUES ('48', '32', '投资洽谈', '投资洽谈', '<p style=\"line-height:150%\"><span style=\"color: rgb(0, 0, 0); font-family: 微软雅黑;\">云采网以其极具竞争力的资源平台，与银行等金融机构合作为客户提供多种成熟高效的融资方案。尤其是在以大宗贸易为切入点，以管理贸易过程为基础，以现金流控制为核心的供应链金融平台。</span></p><p style=\"line-height:150%\"><span style=\"font-family: 微软雅黑; color: rgb(0, 0, 0);\">为客户在采购、生产制造、销售等环节中，依托应收款，预付款、存货等方面全方位提供金融支持，为客户根据项目具体情况进行方案设计，广泛参与产业融合的工作，致力于供应链金融服务，助力客户降低成本、拓展市场。</span></p><p style=\"line-height:150%\"><span style=\"font-family: 微软雅黑; color: rgb(0, 0, 0);\">&nbsp;</span></p><p style=\"line-height:150%\"><span style=\"font-family: 微软雅黑; color: rgb(0, 0, 0);\">如果您有好的项目欢迎洽谈合作，期待您的加入；欢迎与我们联系，共创美好 </span></p><p style=\"line-height:150%\"><span style=\"font-family: 微软雅黑; color: rgb(0, 0, 0);\">电话：028-62507727&nbsp; </span></p><p style=\"line-height:150%\"><span style=\"font-family: 微软雅黑; color: rgb(0, 0, 0);\">邮箱：yy@scxnyf.com</span></p><p><br/></p>', '2016-10-20 17:22:18', '100', '1', '0', '2016-10-23 10:37:14');
